import React, { useState } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import { addEvent } from '../../Action/EventAction';
import { RootState } from '../../Reducer'


function AddEventForm(){

    const [_dateTime, setDateTime] = useState('')
    const [_eventName, setEventName] = useState('')
    const [_memo, setMemo] = useState('')

    const dispatch = useDispatch()

    const onSubmitForm = () => {

        let newEvent = {
            dateTime: _dateTime,
            eventName: _eventName,
            memo: _memo,
            uid: 'e961b9f4-9292-438e-b902-d7cfacd852cc'
        }

        console.log("add ",newEvent)

        dispatch(addEvent(newEvent))
    }

    React.useEffect(() => {
        console.log('add event')
    },[])

    return (
        <div>
            <form>
                <div>
                    <label>date time</label>
                    <input type="datetime-local"
                        onChange={
                            e => setDateTime(e.target.value)
                        }
                    />
                </div>
                <div>
                    <label>Event name</label>
                    <input type="text" placeholder="Event Name"
                        onChange= {
                            e => setEventName(e.target.value)
                        }
                    />
                </div>
                <div>
                    <label>Memo</label>
                    <textarea onChange={
                        e => setMemo(e.target.value)
                    }></textarea>
                </div>
                <div>
                    <button onClick={onSubmitForm}>Submit</button>
                </div>

            </form>
        </div>
    )
}

export default AddEventForm;
