import React, { useRef, useState } from 'react'
import Moment from 'react-moment'
import { useDispatch, useSelector } from 'react-redux'
import { Link, useHistory } from 'react-router-dom';

import { deleteEvent, getEventList } from '../../Action/EventAction'
import { RootState } from '../../Reducer'

import './EventList.scss';

function EventList(){


    let dispatch = useDispatch()


    const event = useSelector((state: RootState) => state.EventReducer.eventList) || []
    const user = useSelector((state: RootState) => state.UserReducer)
    const auth = useSelector((state: RootState) => state.AuthReducer)

    const onDelete = (id?: number) => {
        if(id){
            dispatch(deleteEvent(id))
        }
    }

    React.useEffect(() => {

        if(user.getsuccess&& auth.logingIn){
            console.log('uid : ',user.userInfo?.id)
            dispatch(getEventList(user.userInfo?.id))
        }


        return(
            console.log('terminate')
        )

    }, [dispatch])

    return(
        <div>
            {
            event.map(e => {

                return(
                <>
                    <div>
                        <div className="event-container">
                            <div className="time-container">
                                <div className="top"><Moment format='LL'>{e.dateTime}</Moment></div>
                                <div><Moment format='LT'>{e.dateTime}</Moment></div>
                            </div>
                            <div className="detail-container">
                                <div className="top">{e.eventName}</div>
                                <div><p>{e.memo}</p></div>
                            </div> 
                        </div>

                        <div className="btn-container">
                            <div>
                                <li>
                                    <Link to={`/detail/${e.id}`}>View</Link>
                                </li>
                            </div>
                            <div>   
                                <Link to={`/edit/${e.id}`}>
                                    <button>Edit</button>
                                </Link>
                            </div>
                            <div>
                            <button onClick={() => onDelete(e.id)}>Del</button>
                            </div>
                        </div>

                        <hr></hr>
                    </div>
                </>
                )
            })
            }
        </div>
    )
}

export default EventList
