import React, { useEffect } from 'react'
import {useParams}  from 'react-router-dom'

import { useDispatch, useSelector } from 'react-redux'
import { RootState } from '../../Reducer'
import { getEventDetail } from '../../Action/EventAction';

function EditEventForm(){

    let eventId = useParams();
    let dispatch = useDispatch()
    let event = useSelector((state: RootState) => state.EventReducer)

    console.log("eventId, ",eventId)

    useEffect(() => {

        dispatch(getEventDetail(Number(eventId)))

        return () => {

        }

    }, [])

    return (
        <div>
            <div>
                <label>date time</label>
                <input type="datetime-local" value={event.event?.dateTime}/>
            </div>
            <div>
                <label>Event Name</label>
                <input type="text" value={event.event?.eventName}/>
            </div>
            <div>
                <label>Memo</label>
                <textarea value={event.event?.memo}></textarea>
            </div>
        </div>
    )
}

export default EditEventForm
