import { IEventsState, IEventActionType, IEventAction } from '../model/IEventType'

const initailState: IEventsState = {
    eventList: [],
    event: undefined,
    loading: false,
    error: undefined,
    addSuccess: false,
    getSuccess: false,
    deleteSuccess: false
}

export function EventReducer(state: IEventsState = initailState, action: IEventAction): IEventsState {
    const { type, payload } = action

    switch(type){

        // get all event
        case IEventActionType.GET_ALL_EVENT_START:
            return {
                ...state,
                loading: true,
                error: undefined,
                getSuccess: false
            }
        case IEventActionType.GET_ALL_EVENT_SUCESS:
            return{
                ...state,
                loading: false,
                eventList: payload,
                getSuccess: true
            }
        case IEventActionType.GET_ALL_EVENT_ERROR:
            return {
                ...state,
                loading: false,
                error: payload,
                get
            }

        // get event detail
        case IEventActionType.GET_EVENT_DETAIL_START:
            return{
                ...state,
                loading: true,
                error: undefined
            }

        case IEventActionType.GET_EVENT_DETAIL_SUCCESS:
            return{
                ...state,
                loading: false,
                event: payload
            }

        case IEventActionType.GET_EVENT_DETAIL_ERROR:
            return{
                ...state,
                loading: false,
                error: payload
            }

        // add event
        case IEventActionType.ADD_NEW_EVENT_START:
            return{
                ...state,
                loading: true,
                error: undefined,
                addSuccess: false
            }

        case IEventActionType.ADD_NEW_EVENT_SUCESS:
            return{
                ...state,
                loading: false,
                event: payload,
                addSuccess: true
            }

        case IEventActionType.ADD_NEW_EVENT_ERROR:
            return{
                ...state,
                loading: false,
                error: payload,
                addSuccess: false
            }


        // edit event !!!
        case IEventActionType.UPDATE_EVENT_START:
            return {
                ...state,
                loading: true,
                error: undefined
            }

        case IEventActionType.UPDATE_EVENT_SUCCESS:
            return {
                ...state,
                loading: false,
                event: payload
            }

        // delete event
        case IEventActionType.DELETE_EVENT_START:
            return{
                ...state,
                loading: true,
                error: undefined
            }

        case IEventActionType.DELETE_EVENT_SUCCESS:
            return{
                ...state,
                loading: false,
                event: payload,
                eventList: state.eventList.filter(i => i.id !== payload.id)

            }

        case IEventActionType.DELETE_EVENT_ERORR:
            return{
                ...state,
                loading: false,
                error: payload
            }

        case IEventActionType.SHOW_LOADING:
            return{
                ...state,
                loading: true
            }



        default:
            return state
    }
}
