            }
        }


        return(
            console.log('terminate')
        )

    }, [dispatch])

    return(
        <div>
            {
            event.map(e => {

                return(
                <>
                    <div className="container">
                        <div className="event-container">
                            <div>date time: {e.dateTime}</div>
                            <div>event name: {e.eventName}</div>
                            <div>memo: {e.memo}</div>
                        </div>

                        <div className="btn-container">
                            <div>
                                <li>
                                    <Link to={`/detail/${e.id}`}>View</Link>
                                </li>
                            </div>
                            <div>   
                                <Link to={`/edit/${e.id}`}>
                                    <button>Edit</button>
