    <div className="App"> 

      <div className="header-container">
          <h1>My diary App</h1>
            <nav>
                <ul>
                    <li>
                        <Link to="/events">My Events</Link>
                    </li>
                    <li>
                        <Link to="/addEvent">New Event</Link>
                    </li>
                </ul>
            </nav>
            <Route path="/events">

            </Route>
            <Route path="/addE">

            </Route>
        </div>     

      <div className="event-container">
        {
          event.eventList.map(e => {

            return(
              <>
                <div>
                  <div>date time: {e.dateTime}</div>
                  <div>event name: {e.eventName}</div>
                  <div>memo: {e.memo}</div>
                  <hr></hr>
                </div>
              </>
            )
          })
        }
      </div>


    </div>
  );
}

export default App;
