import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { getEventList } from '../../Action/EventAction'
import { RootState } from '../../Store'


function EventList({uid:string}){

    const [_uid, setUid] = useState<string>(uid);

    let dispatch = useDispatch()
    let event = useSelector((state: RootState) => state.EventReducer) 

    React.useEffect(() => {

        setUid(uid)
        dispatch(getEventList(_uid))

        return(
            console.log('terminate')
        )
    }, [dispatch])

    return(
        <div>
            {
            event.eventList.map(e => {

                return(
                <>
                    <div>
                    <div>date time: {e.dateTime}</div>
                    <div>event name: {e.eventName}</div>
                    <div>memo: {e.memo}</div>
                    <hr></hr>
                    </div>
                </>
                )
            })
            }
        </div>
    )
}

export default EventList
